package Ch2;

public class AnvandEnVariabel {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println("Använda en variabel");
System.out.println();

//Skapa och initiera en variabel
int ursprungsVarde = 5;

//Använda varaibeln
System.out.println("först ursprungsVarde " + ursprungsVarde);
	
// tilldela nytt värde

ursprungsVarde = 12;
System.out.println("sedan ursprungsVarde " + ursprungsVarde);

//skapa ny och initiera
int  j = 6;

System.out.println("\nförst j " + j);
j = ursprungsVarde;
System.out.println("\nsedan j " + j);


	}

}
